mapper.select:
        SELECT


--         级联表的字段
        ,chongwu.chongwu_uuid_number as chongwuUuidNumber
        ,chongwu.chongwu_name as chongwuName
        ,chongwu.chongwu_photo as chongwuPhoto
        ,chongwu.chongwu_video as chongwuVideo
        ,chongwu.chongwu_types as chongwuTypes
        ,chongwu.chongwu_lingyang_types as chongwuLingyangTypes
        ,chongwu.chongwu_delete as chongwuDelete
        ,chongwu.chongwu_content as chongwuContent
        ,yonghu.yonghu_name as yonghuName
        ,yonghu.new_money as newMoney
        ,yonghu.huiyuandengji_types as huiyuandengjiTypes
        ,yonghu.yonghu_sum_jifen as yonghuSumJifen
        ,yonghu.yonghu_new_jifen as yonghuNewJifen

        FROM chongwu_order  a
        left JOIN chongwu chongwu ON a.chongwu_id = chongwu.id
        left JOIN yonghu yonghu ON a.yonghu_id = yonghu.id



                and a.id in

                #{item}



                and a.chongwu_order_uuid_number like CONCAT('%',#{params.chongwuOrderUuidNumber},'%')


                and a.chongwu_id = #{params.chongwuId}


                and a.yonghu_id = #{params.yonghuId}


                and a.chongwu_order_name like CONCAT('%',#{params.chongwuOrderName},'%')


                and a.chongwu_order_phone like CONCAT('%',#{params.chongwuOrderPhone},'%')


                and a.chongwu_order_id_number like CONCAT('%',#{params.chongwuOrderIdNumber},'%')


                and a.chongwu_order_types = #{params.chongwuOrderTypes}


                  and UNIX_TIMESTAMP(a.insert_time) >= UNIX_TIMESTAMP(#{params.insertTimeStart}) 


                  and UNIX_TIMESTAMP(a.insert_time) <= UNIX_TIMESTAMP(#{params.insertTimeEnd}) 



                and chongwu.chongwu_uuid_number like CONCAT('%',#{params.chongwuUuidNumber},'%')


                and chongwu.chongwu_name like CONCAT('%',#{params.chongwuName},'%')


                and chongwu.chongwu_video like CONCAT('%',#{params.chongwuVideo},'%')


                and chongwu.chongwu_types = #{params.chongwuTypes}



                and chongwu.chongwu_lingyang_types = #{params.chongwuLingyangTypes}



                  and chongwu.chongwu_delete >= #{params.chongwuDeleteStart}   


                  and chongwu.chongwu_delete <= #{params.chongwuDeleteEnd}   


                and chongwu.chongwu_delete = #{params.chongwuDelete}


                and chongwu.chongwu_content like CONCAT('%',#{params.chongwuContent},'%')


                and yonghu.yonghu_name like CONCAT('%',#{params.yonghuName},'%')


                  and yonghu.new_money >= #{params.newMoneyStart}   


                  and yonghu.new_money <= #{params.newMoneyEnd}   


                and yonghu.huiyuandengji_types = #{params.huiyuandengjiTypes}



                  and yonghu.yonghu_sum_jifen >= #{params.yonghuSumJifenStart}   


                  and yonghu.yonghu_sum_jifen <= #{params.yonghuSumJifenEnd}   


                  and yonghu.yonghu_new_jifen >= #{params.yonghuNewJifenStart}   


                  and yonghu.yonghu_new_jifen <= #{params.yonghuNewJifenEnd}   



        order by a.${params.orderBy} desc
