mapper.select:
        SELECT


--         级联表的字段

        FROM xihu  a



                and a.id in

                #{item}



                and a.xihu_name like CONCAT('%',#{params.xihuName},'%')


                and a.xihu_types = #{params.xihuTypes}


                  and a.xihu_price >= #{params.xihuPriceStart}   


                  and a.xihu_price <= #{params.xihuPriceEnd}   


                and a.xihu_price = #{params.xihuPrice}


                  and a.xihu_old_money >= #{params.xihuOldMoneyStart}   


                  and a.xihu_old_money <= #{params.xihuOldMoneyEnd}   


                  and a.xihu_new_money >= #{params.xihuNewMoneyStart}   


                  and a.xihu_new_money <= #{params.xihuNewMoneyEnd}   


                  and a.xihu_kucun_number >= #{params.xihuKucunNumberStart}   


                  and a.xihu_kucun_number <= #{params.xihuKucunNumberEnd}   


                and a.xihu_kucun_number = #{params.xihuKucunNumber}


                  and a.xihu_clicknum >= #{params.xihuClicknumStart}   


                  and a.xihu_clicknum <= #{params.xihuClicknumEnd}   


                and a.xihu_clicknum = #{params.xihuClicknum}


                and a.shangxia_types = #{params.shangxiaTypes}


                  and a.xihu_delete >= #{params.xihuDeleteStart}   


                  and a.xihu_delete <= #{params.xihuDeleteEnd}   


                and a.xihu_delete = #{params.xihuDelete}


                and a.xihu_content like CONCAT('%',#{params.xihuContent},'%')




        order by a.${params.orderBy} desc
