mapper.select:
        SELECT


--         级联表的字段
        ,xihu.xihu_name as xihuName
        ,xihu.xihu_photo as xihuPhoto
        ,xihu.xihu_types as xihuTypes
        ,xihu.xihu_price as xihuPrice
        ,xihu.xihu_old_money as xihuOldMoney
        ,xihu.xihu_new_money as xihuNewMoney
        ,xihu.xihu_kucun_number as xihuKucunNumber
        ,xihu.xihu_clicknum as xihuClicknum
        ,xihu.shangxia_types as shangxiaTypes
        ,xihu.xihu_delete as xihuDelete
        ,xihu.xihu_content as xihuContent
        ,yonghu.yonghu_name as yonghuName
        ,yonghu.new_money as newMoney
        ,yonghu.huiyuandengji_types as huiyuandengjiTypes
        ,yonghu.yonghu_sum_jifen as yonghuSumJifen
        ,yonghu.yonghu_new_jifen as yonghuNewJifen

        FROM xihu_order  a
        left JOIN xihu xihu ON a.xihu_id = xihu.id
        left JOIN yonghu yonghu ON a.yonghu_id = yonghu.id



                and a.id in

                #{item}



                and a.xihu_order_uuid_number like CONCAT('%',#{params.xihuOrderUuidNumber},'%')


                and a.xihu_id = #{params.xihuId}


                and a.yonghu_id = #{params.yonghuId}


                  and a.buy_number >= #{params.buyNumberStart}   


                  and a.buy_number <= #{params.buyNumberEnd}   


                and a.buy_number = #{params.buyNumber}


                  and a.xihu_order_true_price >= #{params.xihuOrderTruePriceStart}   


                  and a.xihu_order_true_price <= #{params.xihuOrderTruePriceEnd}   


                and a.xihu_order_types = #{params.xihuOrderTypes}


                and a.xihu_order_payment_types = #{params.xihuOrderPaymentTypes}


                  and UNIX_TIMESTAMP(a.yuyue_time) >= UNIX_TIMESTAMP(#{params.yuyueTimeStart}) 


                  and UNIX_TIMESTAMP(a.yuyue_time) <= UNIX_TIMESTAMP(#{params.yuyueTimeEnd}) 


                  and UNIX_TIMESTAMP(a.insert_time) >= UNIX_TIMESTAMP(#{params.insertTimeStart}) 


                  and UNIX_TIMESTAMP(a.insert_time) <= UNIX_TIMESTAMP(#{params.insertTimeEnd}) 



                and xihu.xihu_name like CONCAT('%',#{params.xihuName},'%')


                and xihu.xihu_types = #{params.xihuTypes}



                  and xihu.xihu_price >= #{params.xihuPriceStart}   


                  and xihu.xihu_price <= #{params.xihuPriceEnd}   


                and xihu.xihu_price = #{params.xihuPrice}


                  and xihu.xihu_old_money >= #{params.xihuOldMoneyStart}   


                  and xihu.xihu_old_money <= #{params.xihuOldMoneyEnd}   


                  and xihu.xihu_new_money >= #{params.xihuNewMoneyStart}   


                  and xihu.xihu_new_money <= #{params.xihuNewMoneyEnd}   


                  and xihu.xihu_kucun_number >= #{params.xihuKucunNumberStart}   


                  and xihu.xihu_kucun_number <= #{params.xihuKucunNumberEnd}   


                and xihu.xihu_kucun_number = #{params.xihuKucunNumber}


                  and xihu.xihu_clicknum >= #{params.xihuClicknumStart}   


                  and xihu.xihu_clicknum <= #{params.xihuClicknumEnd}   


                and xihu.xihu_clicknum = #{params.xihuClicknum}


                and xihu.shangxia_types = #{params.shangxiaTypes}



                  and xihu.xihu_delete >= #{params.xihuDeleteStart}   


                  and xihu.xihu_delete <= #{params.xihuDeleteEnd}   


                and xihu.xihu_delete = #{params.xihuDelete}


                and xihu.xihu_content like CONCAT('%',#{params.xihuContent},'%')


                and yonghu.yonghu_name like CONCAT('%',#{params.yonghuName},'%')


                  and yonghu.new_money >= #{params.newMoneyStart}   


                  and yonghu.new_money <= #{params.newMoneyEnd}   


                and yonghu.huiyuandengji_types = #{params.huiyuandengjiTypes}



                  and yonghu.yonghu_sum_jifen >= #{params.yonghuSumJifenStart}   


                  and yonghu.yonghu_sum_jifen <= #{params.yonghuSumJifenEnd}   


                  and yonghu.yonghu_new_jifen >= #{params.yonghuNewJifenStart}   


                  and yonghu.yonghu_new_jifen <= #{params.yonghuNewJifenEnd}   



        order by a.${params.orderBy} desc
