mapper.select:
        SELECT


--         级联表的字段

        FROM yonghu  a



                and a.id in

                #{item}



                and a.username like CONCAT('%',#{params.username},'%')


                and a.password like CONCAT('%',#{params.password},'%')


                and a.yonghu_name like CONCAT('%',#{params.yonghuName},'%')


                  and a.new_money >= #{params.newMoneyStart}   


                  and a.new_money <= #{params.newMoneyEnd}   


                and a.huiyuandengji_types = #{params.huiyuandengjiTypes}


                  and a.yonghu_sum_jifen >= #{params.yonghuSumJifenStart}   


                  and a.yonghu_sum_jifen <= #{params.yonghuSumJifenEnd}   


                  and a.yonghu_new_jifen >= #{params.yonghuNewJifenStart}   


                  and a.yonghu_new_jifen <= #{params.yonghuNewJifenEnd}   




        order by a.${params.orderBy} desc
